from django.contrib import admin

from oscar.core.loading import get_model

Security = get_model('security', 'Security')
SecurityApplication = get_model('security', 'SecurityApplication')


class SecurityAdmin(admin.ModelAdmin):
    list_display = ('name', 'code', 'usage', 'num_basket_additions',
                    'num_orders', 'total_discount')
    readonly_fields = ('num_basket_additions', 'num_orders', 'total_discount')
    fieldsets = (
        (None, {
            'fields': ('name', 'code', 'usage', 'start_datetime',
                       'end_datetime')}),
        ('Benefit', {
            'fields': ('offers',)}),
        ('Usage', {
            'fields': ('num_basket_additions', 'num_orders',
                       'total_discount')}),
    )


class SecurityApplicationAdmin(admin.ModelAdmin):
    list_display = ('security', 'user', 'order', 'date_created')
    readonly_fields = ('security', 'user', 'order')


admin.site.register(Security, SecurityAdmin)
admin.site.register(SecurityApplication, SecurityApplicationAdmin)
